  /* Grid area layout */

  .name-field {
    grid-area: name;
  }

  .email-field {
    grid-area: email;
  }

  .subject-field {
    grid-area: subject;
  }

  .message-field {
    grid-area: message;
    padding: 4rem 4rem;
  }

  .submit-btn {
    grid-area: submit;

    @include flexbox(center, center);
    cursor: pointer;
    background-color: $footer-txt-clr;
    color: $txt-dark-clr;
    font-weight: bold;
    font-size: 1.5rem;
    transition: all 0.3s ease-in-out;
    &:hover {
      transform: scale(0.95);
      transition: all 0.3s ease-in-out;
    }
  }
}

